        if (! serie) return;

        API.loading('predict', 'Predicting IR spectrum');
        API.createData('explanation', '');
        const history = API.getData('history');
        history.length=0;
        history.triggerChange();

        API.createData('explanation', serie.explanation);
        for (let entry of serie.entries) {
            console.log('Predicting', entry)
            API.loading('predict', 'Predicting IR spectrum for '+entry.name);
            const result = await predict(entry.molfile);
            result.name = entry.name;
            history.push(result);
        }
        recolorHistory(history);
        history.triggerChange();
        API.stopLoading('predict');
        setTimeout( () => {
            API.doAction('selectHistory', -1);
            setTimeout( () => {
                API.doAction('fullOut', 'xy');
            });
        })
        break;
    }
}

function recolorHistory(history) {
    const colors=API.cache('colors');
    for (let i=0; i<history.length; i++) {
        history[i].color = colors[i%colors.length];
    }
}

async function predict(molfile) {
    const molecule = OCL.Molecule.fromMolfile(molfile);

    const nbElectrons = getNumberOfElectrons(molecule);
    if (nbElectrons > 100) {
        UI.showNotification('Too many electrons. The current implementation is limited to 100 electrons');
    }
    const smiles = molecule.toSmiles();
    molecule.addImplicitHydrogens();

    const nbAtoms = molecule.getAllAtoms();

    const molfileSubmitted = molecule.toMolfile();

    const response = await fetch("http://localhost:9000/api/v1/ir", {
        method: "post",
        headers: {
        "content-type": "application/json",
        },
        body: JSON.stringify({
            molFile: molfileSubmitted,
            method: preferences.method,
        }),
    });

    const result = await response.json();

    const atoms={};
    const atomHighlights=[];

    const modes = result.modes;
    for (let i=0; i<modes.length; i++) {
        const mode = modes[i];
        switch(mode.modeType) {
            case 'translation':
                mode.color="pink";
                break;
            case 'rotation':
                mode.color="lightblue";
                break;
            case 'vibration':
                mode.color="lightgreen";
                break;
        }
        const modeID = 'M'+mode.number;
        mode._highlight = [modeID];

        for (let atom of mode.mostContributingAtoms) {
            const id = 'A'+String(i)+'-'+String(atom);
            mode._highlight.push(id);
            atomHighlights.push(id);
            atoms[id]=[atom];
        }
        mode.displacements = {
            type: 'mol3d',
            value: mode.displacements,
            _atoms: atoms,
            _highlight: atomHighlights
        }
    }

    const predicted = {
        x: result.wavenumbers,
        y: result.intensities,
    };

    const expandedMolfile= {
        type: 'mol2d',
        value: molfileSubmitted,
        _atoms: atoms,
        _highlight: atomHighlights,
    };

    const bonds = result.mostRelevantModesOfBonds;
    for (const bond of bonds) {
        bond.bondId = molecule.getBond(bond.startAtom, bond.endAtom);
    }

    return {
        bonds,
        molfile,
        smiles,
        expandedMolfile,
        predicted,
        modes,
        method: preferences.method,
        annotations: createAnnotations(modes),
    }
}

function createAnnotations(modes) {
    const annotations=[];
    modes = modes.sort( (a,b) => (b.wavenumber - a.wavenumber))
    for (let i=0; i<modes.length; i++) {
        const mode = modes[i];
        const highlights = mode._highlight;
        annotations.push({
            position: [
              {
                x: mode.wavenumber - 10,
                y: (10*(i%5)+1)+'px',
              },
              {
                x: mode.wavenumber + 10,
                y: (10*(i%5)+8)+'px',
              }
            ],
            type: 'rect',
            fillColor: 'orange',
            strokeColor: 'transparent',
            _highlight: highlights,
            info: mode,
        });
        annotations.push({
            position: [
              {
                x: mode.wavenumber,
                y: (10*(i%5)+1)+'px',
              },
              {
                x: mode.wavenumber,
                y: (10*(i%5)+8)+'px',
              }
            ],
            type: 'line',
            strokeColor: 'green',
            _highlight: highlights,
        });
    }
    return annotations;
}



