function getNumberOfElectrons(molecule) {
    let mf = new MassTools.MF(molecule.getMolecularFormula().formula);
    let electrons = 0;
    const elements = mf.getElements();
    for (const element of elements) {
        electrons+=element.number * MassTools.elementsObject[element.symbol].number;
    }
    return electrons;
}

