switch (this.action.name) {
    case 'saveGuidedTour': {
        const guidedTour = JSON.stringify(API.getData('guidedTour'));

        const cdb = new CDB();

        let attachements = await cdb.upload({
            data: guidedTour,
            name: 'guidedTour.json',
            contentType: 'application/json'
        });

        UI.showNotification('saved', 'info');
        break;
    }
    case 'reloadGuidedTour': {
        const cdb = new CDB();

        const guidedTour = await cdb.get('guidedTour.json');
        console.log({guidedTour})
        API.createData('guidedTour', guidedTour);

        if(localStorage.getItem('ir-shepherd-tour')) {
            return;
        }

        localStorage.setItem('ir-shepherd-tour', 'yes');
        runGuidedTour(guidedTour);
        break;
    }
    case 'runGuidedTour': {
        const guidedTour = API.getData('guidedTour').resurrect();
        runGuidedTour(guidedTour);
    }
}

function runGuidedTour(guidedTour) {
    const tour = new Shepherd.Tour({
    defaultStepOptions: {
        cancelIcon: {
          enabled: true,
        },
        classes: "class-1 class-2",
        scrollTo: { behavior: "smooth", block: "center" },
      },
    });

    for (let i=0; i<guidedTour.length; i++) {
        const data = guidedTour[i];
        const step = {
            title: data.title,
            text: data.description,
            buttons: [],
        };
        if (i!==0) {
            step.buttons.push( {
              action() {return this.back();},
              text: "Back",
            })
        }
        if (i!==guidedTour.length-1) {
            step.buttons.push( {
              action() {return this.next();},
              text: "Next",
            })
        } else {
            step.buttons.push( {
              action() {return this.next();},
              text: "Complete",
            })
        }

        if (data.selector) {
            step.attachTo = {
                element: data.selector,
                on: data.anchor || "bottom",
            }
        }

        tour.addStep(step);
    }

    tour.start();
}

